<?php	$strSQL = "SELECT * FROM content ORDER BY id DESC";	$rs = mysql_query($strSQL);		while($row = mysql_fetch_array($rs)) {	$id=$row['id'];	   echo "<article><div class='border'><img class=\"sep\" src=\"../template/default/img/sep.png\"><p class=\"title\">" . $row['title'] . "</p>";	   echo "<p class=\"data\">" . $row['data'] . "</p>";	   echo "<p class=\"text\">" . $row['short_article'] . "</p>";	   echo "<div class='readme'><a href='?do=full&id=$id'><p class=\"readmetext\">Read More</p></a></div></div></article>";	  }	?>